// ProfileCard.js
import React, { useEffect, useState } from "react";
import styled from "styled-components";

export const getBackgroundColor = (id) => {
    switch (
        id % 3 // idÏóê Îî∞Îùº Îã§Î•∏ Î∞∞Í≤ΩÏÉâÏùÑ ÏßÄÏ†ï
    ) {
        case 0:
            return "#FFDDC1";
        case 1:
            return "#AEEEEE";
        case 2:
            return "#FFD1DC";
        default:
            return "#AEEEE3";
    }
};

export const getProfileIcon = (id) => {
    switch (
        id % 3 // idÏóê Îî∞Îùº Îã§Î•∏ Î∞∞Í≤ΩÏÉâÏùÑ ÏßÄÏ†ï
