import { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import { getProfile } from "../apis/user";
import styled from "styled-components";
import { getBackgroundColor, getProfileIcon } from "../components/ProfileCard";
import QuestionSection from "../components/QNA/QuestionSection";
import AnswerSection from "../components/QNA/AnswerSection";

function Profile() {
    const [profile, setProfile] = useState(null);
    const [profileIcon, setProfileIcon] = useState("");
    const [activeMenu, setActiveMenu] = useState("질문");

    useEffect(() => {
        if (!profile) return;
        setProfileIcon(getProfileIcon(profile.id));
    }, [profile]);

    useEffect(() => {
        async function fetchData() {
            try {
                const profile = await getProfile();
                setProfile(profile);
            } catch (error) {
                console.error(error);
            }
        }
        fetchData();
    }, []);
    return (
        <>
            <Navbar />
            <Wrapper>
                <UserProfile>
                    <ProfileImage id={profile?.id}>{profileIcon}</ProfileImage>
                    <UserName>{profile?.username}</UserName>
                    <UserBio>{profile?.bio}</UserBio>
                </UserProfile>
                <QNAContainer>
                    <Menu>
                        <MenuButton
                            active={activeMenu === "질문"}
                            onClick={() => setActiveMenu("질문")}
                        >
                            질문
                        </MenuButton>
                        <MenuButton
                            active={activeMenu === "답변"}
                            onClick={() => setActiveMenu("답변")}
                        >
                            답변
                        </MenuButton>
                    </Menu>
                    {activeMenu === "질문" ? (
                        <QuestionSection />
                    ) : (
                        <AnswerSection />
                    )}
                </QNAContainer>
            </Wrapper>
        </>
    );
}

export default Profile;
const Wrapper = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    background: linear-gradient(135deg, #e0f7fa, #ffffff);
    padding: 50px 20px;
    font-family: 'Poppins', sans-serif;
    min-height: 100vh;
`;

const UserProfile = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 20px;
    background: rgba(255, 255, 255, 0.7);
    padding: 40px;
    border-radius: 25px;
    backdrop-filter: blur(10px);
    box-shadow: 0px 10px 20px rgba(0, 0, 0, 0.15);
    margin-bottom: 40px;
    transition: all 0.3s ease;

    &:hover {
        transform: translateY(-5px);
        box-shadow: 0px 20px 30px rgba(0, 0, 0, 0.2);
    }
`;

const ProfileImage = styled.div`
    background-color: ${(props) => getBackgroundColor(props.id)};
    border-radius: 50%;
    width: 160px;
    height: 160px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 80px;
    color: #ffffff;
    box-shadow: 0px 6px 15px rgba(0, 0, 0, 0.1);
    transition: transform 0.3s ease;

    ${UserProfile}:hover & {
        transform: scale(1.1);
    }
`;

const UserName = styled.h2`
    font-size: 28px;
    color: #222;
    font-weight: bold;
    text-align: center;
    letter-spacing: 1.2px;
`;

const UserBio = styled.p`
    font-size: 16px;
    color: #555;
    text-align: center;
    max-width: 500px;
    line-height: 1.6;
    opacity: 0.9;
`;

const Menu = styled.div`
    display: flex;
    justify-content: center;
    gap: 25px;
    margin-bottom: 30px;
`;

const MenuButton = styled.button`
    font-size: 20px;
    font-weight: 600;
    color: ${(props) => (props.active ? "#007AFF" : "#999")};
    background: ${(props) =>
        props.active ? "rgba(0, 122, 255, 0.1)" : "transparent"};
    border: none;
    border-bottom: ${(props) =>
        props.active ? "3px solid #007AFF" : "3px solid transparent"};
    border-radius: 8px;
    cursor: pointer;
    padding: 12px 25px;
    transition: all 0.3s ease;

    &:hover {
        color: #007AFF;
        background: rgba(0, 122, 255, 0.1);
    }
`;

const QNAContainer = styled.div`
    display: flex;
    flex-direction: column;
    width: 100%;
    max-width: 800px;
    padding: 30px;
    background: rgba(255, 255, 255, 0.7);
    border-radius: 25px;
    backdrop-filter: blur(8px);
    box-shadow: 0px 10px 20px rgba(0, 0, 0, 0.1);
`;
