import { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import { getProfile } from "../apis/user";
import styled from "styled-components";
import { getBackgroundColor, getProfileIcon } from "../components/ProfileCard";
import QuestionSection from "../components/QNA/QuestionSection";
import AnswerSection from "../components/QNA/AnswerSection";

function Profile() {
    const [profile, setProfile] = useState(null);
    const [profileIcon, setProfileIcon] = useState("");
    const [activeMenu, setActiveMenu] = useState("질문");

    useEffect(() => {
        if (!profile) return;
        setProfileIcon(getProfileIcon(profile.id));
    }, [profile]);

    useEffect(() => {
        async function fetchData() {
            try {
                const profile = await getProfile();
                setProfile(profile);
            } catch (error) {
                console.error(error);
            }
        }
        fetchData();
    }, []);
    return (
        <>
            <Navbar />
            <Wrapper>
                <UserProfile>
                    <ProfileImage id={profile?.id}>{profileIcon}</ProfileImage>
                    <UserName>{profile?.username}</UserName>
                    <UserBio>{profile?.bio}</UserBio>
                </UserProfile>
                <QNAContainer>
                    <Menu>
                        <MenuButton
                            active={activeMenu === "질문"}
                            onClick={() => setActiveMenu("질문")}
                        >
                            질문
                        </MenuButton>
                        <MenuButton
                            active={activeMenu === "답변"}
                            onClick={() => setActiveMenu("답변")}
                        >
                            답변
                        </MenuButton>
                    </Menu>
                    {activeMenu === "질문" ? (
                        <QuestionSection />
                    ) : (
                        <AnswerSection />
                    )}
                </QNAContainer>
            </Wrapper>
        </>
    );
}

export default Profile;
const CardContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 20px;
    border-radius: 20px;
    background: linear-gradient(145deg, #ffffff, #f3f3f3);
    box-shadow: 10px 10px 20px rgba(0, 0, 0, 0.1), -5px -5px 15px rgba(255, 255, 255, 1);
    cursor: pointer;
    transition: all 0.3s ease;
    transform: translateY(0);

    &:hover {
        transform: translateY(-10px);
        box-shadow: 15px 15px 30px rgba(0, 0, 0, 0.15), -10px -10px 20px rgba(255, 255, 255, 1);
    }
`;

const ProfileImage = styled.div`
    background-color: ${(props) => getBackgroundColor(props.id)};
    border-radius: 50%;
    width: 100px;
    height: 100px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 50px;
    margin-bottom: 10px;
    box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
    transition: all 0.3s ease;

    ${CardContainer}:hover & {
        transform: scale(1.1);
    }
`;

const UserName = styled.h2`
    font-size: 20px;
    font-weight: bold;
    color: #333;
    margin: 10px 0 5px 0;
    text-align: center;
`;

const UserBio = styled.p`
    font-size: 14px;
    color: #777;
    text-align: center;
    margin-bottom: 10px;
`;

const QuestionCount = styled.p`
    font-size: 14px;
    color: #999;
    text-align: center;
`;
